actions.script:
var FishPop1 = runtime.globalVars.fish_pop1;
var FishPop2 = runtime.globalVars.fish_pop2;
var fishProb = (FishPop1/2 * .75) + (FishPop2/2 * .25);

if(Math.random()*100 < fishProb)
{

	var Catch = Math.random() * 100;
	var RodLvl = runtime.globalVars.rod_level; 
	var Maintanence = runtime.globalVars.maintance_level;
	var Modifier = Math.floor(RodLvl/2 * Maintanence/7);
	var Modifier2 = Math.floor(RodLvl/3 * Maintanence/7);	
	//console.log("catch: " + Catch );
	//console.log("modifer: " + Modifier );
	if(Catch < 10+Modifier2)
	{
		runtime.globalVars.fish_amount2 +=1+Modifier;
		runtime.globalVars.fish_pop2 -=.1+Modifier;
		console.log("+1 fish2");
	}
	else if(Catch < 30+Modifier2)
	{
		runtime.globalVars.fish_amount1 +=2+Modifier;
		runtime.globalVars.fish_pop1 -=.2+Modifier;		
		console.log("+2 fish1");
	}
	else
	{
		runtime.globalVars.fish_amount1 +=1+Modifier;	
		runtime.globalVars.fish_pop1 -=0.1+Modifier;
		console.log("+1 fish1");
	}
	Main.PlaySplash();
}
else
{
	console.log("No luck");
}

