var catchProb = Math.random()*100
var Maintanence = runtime.globalVars.maintance_level;
if(catchProb < runtime.globalVars.net_level * Maintanence/7)
{
	runtime.globalVars.fish_amount1 += 1;
	runtime.globalVars.fish_pop1 -=0.1+Modifier;	
	Main.PlaySplash();
}

var PLocation = runtime.globalVars.player_location;
var Maintance = runtime.globalVars.maintance_level;
var BLevel = runtime.globalVars.boat_level;
if(PLocation==1)
{
	runtime.globalVars.travel_time_l1=0;
}
else if(PLocation==2)
{
	runtime.globalVars.travel_time_l2=0;
}
else if(PLocation==3)
{
	runtime.globalVars.travel_time_l3=0;
}

var location = runtime.globalVars.player_location;
if(location==1)
{
	runtime.globalVars.CurrentTime += runtime.globalVars.travel_time_l1 * 60;
}
else if(location==2)
{
	runtime.globalVars.CurrentTime += runtime.globalVars.travel_time_l2 * 60;
}
else if(location==3)
{
	runtime.globalVars.CurrentTime += runtime.globalVars.travel_time_l3 * 60;
}
else
{
	runtime.globalVars.CurrentTime += 60;
}

var Fish1 = runtime.globalVars.fish_amount1;
var Fish2 = runtime.globalVars.fish_amount2;
var Fish3 = runtime.globalVars.fish_amount3;
if(Fish1==0 && Fish2==0 && Fish3==0)
{
	runtime.callFunction("InitShop");
	runtime.objects.CloseM.getFirstInstance().isVisible=false;
}

var Fish1 = runtime.globalVars.fish_amount1;
var Fish2 = runtime.globalVars.fish_amount2;
var Fish3 = runtime.globalVars.fish_amount3;
runtime.globalVars.Fish_Demand1 = 0;
runtime.globalVars.Fish_Demand2 = 0;
runtime.globalVars.Fish_Demand3 = 0;
if(Fish1>0)
{
	runtime.globalVars.Fish_Demand1 = Math.floor(Math.random() * (Fish1*.2 - 1 + 1) + 1);
}

if(Fish2>0)
{
	runtime.globalVars.Fish_Demand2 = Math.floor(Math.random() * (Fish2*.2 - 1 + 1) + 1);
}

if(Fish3>0)
{
	runtime.globalVars.Fish_Demand3 = Math.floor(Math.random() * (Fish3*.2 - 1 + 1) + 1);
}

var Level = runtime.globalVars.boat_level;
var Text = runtime.objects.BoatLvl.getFirstInstance();
var Button = runtime.objects.UpgradeBoat.getFirstInstance();
if(Level>=10)
{
	Text.text = "Boat Lvl MAX";
	Button.isVisible= false;	
}
else
{
	Text.text = "Boat Lvl: " + Level;
	Button.isVisible= true;	
	Button.isEnabled= true;
}

var Level = runtime.globalVars.rod_level;
