	Button.isVisible= true;	
	Button.isEnabled= true;
}

var MentalLevel = runtime.globalVars.motivation;
var TotalFish = runtime.globalVars.fish_caught;
var FishSold = TotalFish - (runtime.globalVars.fish_amount1 + runtime.globalVars.fish_amount2 + runtime.globalVars.fish_amount3);
if(FishSold/TotalFish <.7 || TotalFish < 50)
{
	MentalLevel -= 10;
}
else
{
	MentalLevel += 5;
}
Math.min(Math.max(MentalLevel, 0), 100);
runtime.globalVars.motivation = MentalLevel;
